        function validateForm() {
            var heightSelect = document.getElementById("height");
            var weightSelect = document.getElementById("weight");
            var validationMessage = document.getElementById("validationMessage");

            // Reset error message
            validationMessage.innerHTML = "";

            // Validate height
            if (heightSelect.value === "height") {
                validationMessage.innerHTML = "Please select your height before proceeding.";
                return false;
            }

            // Validate weight
            if (weightSelect.value === "null") {
                validationMessage.innerHTML = "Please select your weight before proceeding.";
                return false;
            }
            calculateScore(); // Calculate the score without redirecting yet

            // If all validations pass, proceed to the next page
            return true;
        }

        function calculateScore() {
        var heightValue = document.getElementById("height").value;
        var weightValue = document.getElementById("weight").value;
        var score5 = parseInt(getParameterByName('score5')) || 0; // Get score from test5.html

        // Calculate BMI
        var heightInMeters = heightToMeters(heightValue);
        var weightInKg = weightToKg(weightValue);
        var bmi = calculateBMI(heightInMeters, weightInKg);
        alert('bmi='+ bmi + 'bmiscore='+ bmiScore + 'heightInMeters' +heightInMeters + 'weightInKg' +weightInKg);
        // Assign scores based on BMI
        var bmiScore = calculateBMIScore(bmi);

        // Calculate height and weight scores

        // Calculate total score
        var totalScore = score5 + bmiScore;

        // Pass data to scorepage.html
        window.location.href = 'scorepage.html?totalScore=' + totalScore;
    }

    function heightToMeters(heightValue) {
        // Convert height to meters based on the selected value
        // You need to implement your own conversion logic
        // Example: Convert 5'4" to meters
        return 1.63;
    }

    function weightToKg(weightValue) {
        // Convert weight to kilograms based on the selected value
        // You need to implement your own conversion logic
        // Example: Convert <159 lbs to kilograms
        return 72;
    }

    function calculateBMI(heightInMeters, weightInKg) {
        // Calculate BMI
        const bmi = weightInKg / (heightInMeters * heightInMeters);
        return bmi
    }

    function calculateBMIScore(bmi) {
        // Assign scores based on BMI categories
        if (bmi < 23) {
            return 0;
        } else if (bmi >= 23 && bmi <= 29) {
            return 1;
        } else if (bmi >= 30 && bmi <= 39) {
            return 2;
        } else {
            return 3;
        }
    }

    function calculateHeightScore(heightValue) {
        // Your logic for calculating height score
        // Example logic: Assign a score based on height
        if (heightValue === "4' 10\"") {
            return 1;
        } else if (heightValue === "4' 11\"") {
            return 2;
        } else {
            return 0;
        }
    }

    function calculateWeightScore(weightValue) {
        // Your logic for calculating weight score
        // Example logic: Assign a score based on weight
        if (weightValue === "%3c159") {
            return 1;
        } else if (weightValue === "159-190") {
            return 2;
        } else {
            return 0;
        }
    }

    // Function to get URL parameter by name
    function getParameterByName(name) {
        var url = window.location.href;
        name = name.replace(/[\[\]]/g, "\\$&");
        var regex = new RegExp("[?&]" + name + "(=([^&#]*)|&|#|$)"),
            results = regex.exec(url);
        if (!results) return null;
        if (!results[2]) return '';
        return decodeURIComponent(results[2].replace(/\+/g, ' '));
    }
