function validateForm() {
        var heightSelect = document.getElementById("height");
        var weightSelect = document.getElementById("weight");

        var heightValue = heightSelect.value;
        var weightValue = weightSelect.value;

        if (heightValue === "" || weightValue === "") {
            document.getElementById("validationMessage").innerText = "Please select both height and weight.";
            return;
        }

        // Calculate BMI
        var bmi = calculateBMI(heightValue, weightValue);

        // Display BMI results
        displayBMIResults(bmi);
    }

    function calculateBMI(height, weight) {
    // Convert height to inches
    var heightInInches = parseInt(height.split("'")[0]) * 12 + parseInt(height.split("'")[1].split('"')[0]);

    // Convert weight to kilograms
    var weightInKg = isNaN(parseInt(weight)) ? null : parseInt(weight) * 0.453592;

    // Log the values for debugging
    console.log("Height in inches:", heightInInches);
    console.log("Weight in kg:", weightInKg);

    // Calculate BMI
    var bmi = (weightInKg / Math.pow(heightInInches / 39.37, 2)).toFixed(2);
    var points = interpretBMI(bmi);

// Display points
console.log("Points:", points);
    // Log the BMI value for debugging
    console.log("BMI:", bmi);

    return bmi;
    var points = interpretBMI(bmi);

    // Display points
    console.log("Points:", points);
}
function interpretBMI(bmi) {
    if (bmi < 23) {
        return 0;
    } else if (bmi >= 23 && bmi <= 29) {
        return 1;
    } else if (bmi >= 30 && bmi <= 39) {
        return 2;
    } else {
        return 3;
    }
}


